import React, { useRef, useEffect } from "react";
import { MDBBtn, MDBContainer, MDBRow, MDBCol } from "mdb-react-ui-kit";

import house from "../images/Bank-of-England-House-Illustration.png";
import customer1 from "../images/Bank-of-England-Customer.jpg";
import signing from "../images/Bank-of-England-Signing.jpg";
import Reviews from "../components/Reviews";
import LoanCards from "../components/LoanCards";
import LightBanner from "../components/LightBanner";

import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

export default function Home() {

  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.fromTo(
      element.querySelector("#move"),
      {
        opacity: 0,
        y: 940,
      },
      {
        opacity: 1,
        y: 0,
        scrollTrigger: {
          trigger: element.querySelector("#here"),
          start: "top top",
          end: "center center",
          scrub: true,
        },
      }
    );
  }, []);

  useEffect(() => {
    const element = ref.current;
    gsap.fromTo(
      element.querySelector("#ma__banner__house"),
      {
        opacity: 1,
        x: 2940,
      },
      {
        opacity: 1,
        x: 0,
        scrollTrigger: {
          trigger: element.querySelector("#here"),
          start: "top center",
          end: "center center",
          scrub: true,
        },
      }
    );
  }, []);
  
  return (
    <div ref={ref}>
    <MDBContainer fluid style={{maxWidth:'2000px'}}>
      <MDBContainer
        className=""
        style={{marginTop: "100px"}}
      >
        <MDBRow className="g-2">
          <MDBCol lg="6" className="user-select-none mb-3">
            <div style={{ paddingLeft: "15px" }}>
              <h1
                class="text-md-start"
                style={{ fontWeight: "bold", color: "black" }}
              >
                <div
                  style={{
                    color: "#215732",
                    width: "50px",
                    height: "7px",
                    background: "#215732",
                    marginBottom: "15px",
                  }}
                />
                Bank of Englad Texas is the white glove home loan experiance
                designed for you
              </h1>
              <h5 class="text-left">
                The home buying experience shouldn't be a hassle. With BOE Texas
                it won't be. Start an application to see what's possible.
              </h5>
            </div>
            <div className="text-center">
              <MDBBtn
                tag="a"
                className="m-2 p-3"
                style={{
                  fontWeight: "600",
                  width: "300px",
                  fontSize: "16px",
                }}
                href="https://boetexas.floify.com/apply-now"
              >
                Apply For Home Purchase
              </MDBBtn>
            </div>
            <div className="text-center">
              <MDBBtn
                tag="a"
                className="m-2 p-3"
                style={{
                  fontWeight: "600",
                  width: "300px",
                  fontSize: "16px",
                }}
                href="#"
              >
                Apply For Refinance
              </MDBBtn>
            </div>
          </MDBCol>
          <MDBCol
            lg="6"
            style={{
              backgroundImage: `url(${customer1})`,
              backgroundRepeat: "no-repeat",
              backgroundPosition: "top center",
              backgroundSize: "cover",
              minHeight: "40vh",
            }}
          ></MDBCol>
        </MDBRow>
      </MDBContainer>
      <LightBanner />
      <MDBContainer className="mt-3" style={{ maxWidth: "2500px" }}>
        <MDBRow className="g-2">
          <MDBCol lg="6" className="g-0">
            <img src={signing} class="img-fluid mb-4"></img>
          </MDBCol>
          <MDBCol lg="6" className="">
            <div style={{ marginLeft: "15px" }}>
              <h1
                class="text-md-start"
                style={{ fontWeight: "bold", color: "black" }}
              >
                <div
                  style={{
                    color: "#215732",
                    width: "50px",
                    height: "7px",
                    background: "#215732",
                    marginBottom: "15px",
                  }}
                />
                Connect with a lender in your community.
              </h1>
              <h5 class="text-left">
                A mortgage broker can be your trusted guide close to home. They
                can really get to know you and tailer loan options to meet your
                needs.
              </h5>
            </div>
            <div className="text-center mt-4">
              <MDBBtn
                tag="a"
                rippleColor="success"
                className="m-2"
                style={{
                  color: "grey",
                  fontWeight: "600",
                  backgroundColor: "white",
                  border: "2px solid grey",
                  padding: "20px",
                  fontSize: "16px",
                }}
              >
                Connect With Local Lender
              </MDBBtn>
            </div>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
      <LoanCards />
      <MDBContainer
        style={{ backgroundColor: "#4e4e4e", maxWidth: "2500px" }}
        className="mt-5 mb-5"
      >
        <MDBRow
          className="d-flex justify-content-center align-items-center p-5"
          style={{ color: "white" }}
        >
          <MDBCol sm="4">
            <h1 style={{ fontWeight: "bold" }} id="move">
              Your Mortgage Approval Starts Here
            </h1>
            <p>
              Our streamlined application syncs with your bank to get you
              accurate numbers fast.
            </p>
            <MDBBtn
              tag="a"
              rippleColor="success"
              href="https://boetexas.floify.com/apply-now"
              style={{
                color: "black",
                fontWeight: "600",
                backgroundColor: "white",
                padding: "15px 20px 15px 20px",
                fontSize: "14px",
              }}
            >
              Start My Approval
            </MDBBtn>
          </MDBCol>
          <MDBCol sm="4" className="text-center">
            <img src={house} class="img-fluid" id="ma__banner__house"  />
          </MDBCol>
        </MDBRow>
      </MDBContainer>
      <Reviews />
      </MDBContainer>
      </div>
  );
}
