import React, { useRef, useEffect } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
} from "mdb-react-ui-kit";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

export default function Policies() {
  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.fromTo(
      element.querySelector("#move"),
      {
        opacity: 0,
        y: -340,
      },
      {
        opacity: 1,
        y: -10,
        scrollTrigger: {
          trigger: element.querySelector("#here"),
          start: "top center",
          end: "center center",
          scrub: true,
        },
      }
    );
  }, []);

  useEffect(() => {
    const element = ref.current;
    gsap.fromTo(
      element.querySelector("#ma__banner__house"),
      {
        opacity: 1,
        x: 640,
      },
      {
        opacity: 1,
        x: 140,
        scrollTrigger: {
          trigger: element.querySelector("#ma__banner__house__1"),
          start: "top center",
          end: "center center",
          scrub: true,
        },
      }
    );
  }, []);

  return (
    <div ref={ref}>
      <MDBContainer>
        <MDBRow>
          <MDBCol sm="12">Website Analytics Policy</MDBCol>
          <MDBCol>
            BOE Mortgage was founded on principles of trust, integrity and the
            delivery of outstanding service. Therefore, we are very concerned
            with the privacy rights of our online visitors and customers and are
            committed to protecting the information collected about you. This
            website, BOEDallas.com, is owned and operated by Bank of England
            Mortgage, has taken extensive measures to protect the
            confidentiality of your personal information and to protect your
            data from misuse and unauthorized access or disclosure.
            Unfortunately, no data transmission over the Internet can be
            guaranteed to be 100% secure. As a result, although we take great
            measures to protect your information, BOE Mortgage cannot ensure or
            warrant the security of any information you transmit to us, and you
            do so at your own risk. Once we receive your transmission, BOE
            Mortgage takes industry standard efforts to safeguard the
            confidentiality of your information, such as secure, 128 bit
            encryption on our applications. We also restrict internal access of
            stored data to select employees that have been informed of our
            privacy policies. If you have concerns involving the security or
            privacy of your information, you can arrange to talk to one of our
            loan specialists.
          </MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
        <MDBRow>
          <MDBCol></MDBCol>
          <MDBCol></MDBCol>
        </MDBRow>
      </MDBContainer>
    </div>
  );
}
